import "./Map.css";
import { useRef, useState, useEffect } from 'react';
import Map, { NavigationControl, Source, Layer } from "react-map-gl";
import { MAPBOX_TOKEN, BACKEND_SERVER } from './Const'
import GeocoderControl from './GeocoderControl';
import { clusterLayer, clusterCountLayer, unclusteredPointLayer, routeLayer } from './layers';
import EventInfo from "./EventInfo";
import useFetch from "../Api/Api"
import { Spin } from 'antd';


function Mapbox() {
  const mapRef = useRef(null);
  const [popupInfo, setPopupInfo] = useState(null);

  const {data: routeData, loading: routeLoading, error: routeFetchError} = useFetch(BACKEND_SERVER + 'routes')
  const routeGeojson = useRef(null);
  useEffect(() => {
    if (!routeLoading) {
      console.log('load complete', routeLoading)
      if (routeData.success) {
        routeGeojson.current = routeData.res.map((route) => {
          return {
            "type": "FeatureCollection",
            "features": [{
              "type": "Feature",
              "properties": {
                "name": route.name,
              },
              "geometry": route.geometry
            }]
          }
        })[0]   // might change if multiple route applies
      }
    }
  }, [routeLoading, routeData])

  const {data: eventData, loading: eventLoading, error: eventFetchError} = useFetch(BACKEND_SERVER + 'events')
  const eventGeojson = useRef(null);
  useEffect(() => {
    if (!eventLoading) {
      console.log('event load complete', eventLoading)
    }
  }, [eventLoading, eventData])

  const [mapLoading, setMapLoading] = useState(true)
  useEffect(() => {
    console.log(routeLoading, eventLoading, !routeLoading && !eventLoading)
    setMapLoading(!routeLoading && !eventLoading)
  }, [routeLoading, eventLoading]) 

  const onClick = event => {
    const feature = event.features[0];
    if (!feature) {
      return
    }
    if (feature.layer.id === 'clusters') {
      const clusterId = feature.properties.cluster_id;
      mapRef.current.getSource('event').getClusterExpansionZoom(clusterId, (err, zoom) => {
        if (err) {
          return;
        }
        mapRef.current.easeTo({
          center: feature.geometry.coordinates,
          zoom,
          duration: 500
        });
      });
    }
    if (feature.layer.id === 'unclustered-point') {
      console.log('point:', feature)
      const coordinates = feature.geometry.coordinates.slice();
      setPopupInfo({
        longitude: coordinates[0],
        latitude: coordinates[1],
        properties: feature.properties
      });
    }
  };

  return (
    <>
      {mapLoading && 
        <Spin tip="Loading" size="large" className="spin"/>
      }
      {!mapLoading && 
        <Map
          initialViewState={{
            longitude: -74.6,
            latitude: 40.4,
            zoom: 10,
          }}
          mapboxAccessToken={MAPBOX_TOKEN}
          style={{ position: "absolute", width: "100%", top: 0, bottom: 0 }}
          mapStyle="mapbox://styles/mapbox/streets-v12"
          onClick={onClick}
          ref={mapRef}
          interactiveLayerIds={[clusterLayer.id, unclusteredPointLayer.id]}
        >
          <NavigationControl position="top-left" />
          <GeocoderControl position="top-right" />
          <Source
            id="event"
            type="geojson"
            // data="https://docs.mapbox.com/mapbox-gl-js/assets/earthquakes.geojson"
            data = "./data/sample.geojson"
            cluster={true}
            clusterMaxZoom={14}
            clusterRadius={50}
          >
            <Layer {...clusterLayer} />
            <Layer {...clusterCountLayer} />
            <Layer {...unclusteredPointLayer} />
          </Source>
          <Source
            id="route"
            type="geojson"
            data={ routeGeojson.current }
            // data="./data/route.geojson"
          >
            <Layer {...routeLayer}/>
          </Source>
          {popupInfo && (
            <EventInfo
              longitude={popupInfo.longitude}
              latitude={popupInfo.latitude}
              properties={popupInfo.properties}
              onClose={() => setPopupInfo(null)}
            />
          ) 
          }
        </Map>
      }
    </>
  );
}

export default Mapbox;
